mapper.select:
select us.*,q.comq_name from pro_company_user us LEFT JOIN pro_company_qua q ON q.comq_id=us.comq_id
and us.comq_id = #{comqId,jdbcType=VARCHAR}
and us.user_name like CONCAT('%', #{userName}, '%')

    select * from pro_company_user us LEFT JOIN pro_certificate c ON us.user_id=c.fkcert_id
    
    	  
	         and us.comq_id = #{comqId,jdbcType=VARCHAR}
	      
	      
	         and us.user_id = #{userId,jdbcType=VARCHAR}
	      
	      
	         and us.user_name = #{userName,jdbcType=VARCHAR}
	      
	      
	         and us.user_tel = #{userTel,jdbcType=VARCHAR}

mapper.update:
